class Add extends Component {
	constructor() {
		super();
		this.state = {name: 'Иван'};
	}
	changeName() {
		this.setState({name: 'Коля'});
	}

	render() {
		//Выводим имя и кнопку для изменения:
		return <div>
			<p>имя: {this.state.name}</p>
			<button onClick={this.changeName.bind(this)}>сменить имя</button>
		</div>;
	}
}

export default Add;
